// 1) Read outputs from both HTTP nodes
const trendsJson = $items('Get Google Trends')[0].json;
const keywordsJson = $items('Get Keywords')[0].json;

const topics = [];

// 2) From Google Trends (RELATED_QUERIES)
const rq = trendsJson.related_queries || {};
const sets = [
  ...(rq.top || []),
  ...(rq.rising || []),
];

for (const item of sets) {
  if (!item.query) continue;
  topics.push({
    topic: item.query,
    trendScore: item.value ?? null,
    source: 'trends',
  });
}

// 3) From Google Search related_searches via SerpAPI
for (const rs of (keywordsJson.related_searches || [])) {
  if (!rs.query) continue;
  topics.push({
    topic: rs.query,
    trendScore: null,
    source: 'related_search',
  });
}

// 4) Preserve config fields from Edit Fields
return [{
  json: {
    topics,
    niche: $json.niche,
    targetAudience: $json.targetAudience,
    brandVoice: $json.brandVoice,
    wpBaseUrl: $json.wpBaseUrl,
  }
}];

// The agent returns: { output: "<JSON string>" }
const raw = $json.output || $json;   // handle both cases

// If it's already an array, keep it; otherwise parse the string
const arr = Array.isArray(raw) ? raw : JSON.parse(raw);

// Return one item per topic so Split In Batches can use it
return arr.map(topic => ({ json: topic }));

// Brief AI Agent returns something like { output: "<JSON string>" }
// or directly the JSON object. Normalize it.

const raw = $json.output || $json;

// If already an object, just return it.
if (raw && typeof raw === 'object') {
  return [{ json: raw }];
}

// If it's a string, parse it as JSON.
if (typeof raw === 'string') {
  // Trim whitespace
  let cleaned = raw.trim();

  // Optionally strip leading `````` and trailing ```
  if (cleaned.startsWith('```')) {
    // Remove first line (``````json)
    const lines = cleaned.split('\n');
    lines.shift();
    // If last line is ```
    if (lines[lines.length - 1].trim() === '```') {
      lines.pop();
    }
    cleaned = lines.join('\n').trim();
  }

  const obj = JSON.parse(cleaned);
  return [{ json: obj }];
}

// Fallback
throw new Error('Unexpected brief format: ' + typeof raw);

