      metaDescription: raw.metaDescription,
      slug: raw.slug,
      faqSection: raw.faqSection,
    },
  }];
}

// From here on, raw is a string like ``````
let text = raw.trim();

// Remove ```
if (text.startsWith('```')) {
  const lines = text.split('\n');
  // Remove first line: ``````)
  lines.shift();

  // If last line is ```
  if (lines.length && lines[lines.length - 1].trim() === '```') {
    lines.pop();
  }

  text = lines.join('\n').trim();
}

// Now text is still not valid JSON because optimizedArticleHtml contains raw newlines.
// Extract fields with simple regex instead of JSON.parse.

function matchField(name) {
  const re = new RegExp(`"${name}"\\s*:\\s*"([\\s\\S]*?)"\\s*(,|\\n)`);
  const m = text.match(re);
  return m ? m[1] : '';
}

const optimizedArticleHtml = matchField('optimizedArticleHtml').trim();
const seoTitle = matchField('seoTitle');
const metaDescription = matchField('metaDescription');
const slug = matchField('slug');

// faqSection is an array; parse just that slice
let faqSection = [];
const faqMatch = text.match(/"faqSection"\s*:\s*(\[[\s\S]*\])\s*\n?\}/);

if (faqMatch) {
  try {
    faqSection = JSON.parse(faqMatch[1]);
  } catch (e) {
    faqSection = [];
  }
}

return [{
  json: {
    optimizedArticleHtml,
    seoTitle,
    metaDescription,
    slug,
    faqSection,
  },
}];

