// Get all merged items from Merge
const items = $items();

// Use the first merged item as representative
const first = items[0].json;

// Covert Date Format to Day and Time in IST
const now = new Date();
const options = {
  day: '2-digit',
  month: 'short',
  year: 'numeric',
  hour: '2-digit',
  minute: '2-digit',
  second: '2-digit',
  hour12: true,
  timeZone: 'Asia/Kolkata',
};
const formattedRunDate = now.toLocaleString('en-GB', options).replace(',', '');

// Build one summary object for this entire run
return [
  {
    json: {
      runDate: formattedRunDate + ' IST',
      wordpress_topicTitle: first.wordpress_topicTitle || $('Post Snapshot').first().json.seoTitle,
      wordpress_postUrl: $('Post Snapshot').first().json.postUrl,
      wordpress_imageUrl: $('Post Snapshot').first().json.imageUrl,
      wordpress_metaDescription: $('Post Snapshot').first().json.metaDescription,
      linkedinPost: $('Parse Multi‑Channel Output').first().json.linkedinPost,
